import "gantt-schedule-timeline-calendar/dist/style.css"; // Import the library's styles
import { Gantt } from "gantt-schedule-timeline-calendar"; // Import the library

export default {
  name: "App",
  mounted() {
    // Initialize the Gantt chart
    const gstc = Gantt({
      element: document.getElementById("gantt"),
      // Attach to an HTML element
      state: {
        config: {
          list: {
            rows: {
              // Define the rows for the Gantt chart
              P001: {
                id: "P001",
                label: "Product A"
              },
              P002: {
                id: "P002",
                label: "Product B"
              }
            }
          },
          chart: {
            items: {
              // Define the items (tasks) in the chart
              task1: {
                id: "task1",
                label: "Selling Product A",
                time: {
                  start: new Date("2024-12-01").getTime(),
                  end: new Date("2024-12-10").getTime()
                },
                rowId: "P001"
              },
              task2: {
                id: "task2",
                label: "Selling Product B",
                time: {
                  start: new Date("2024-12-02").getTime(),
                  end: new Date("2024-12-07").getTime()
                },
                rowId: "P002"
              }
            }
          }
        }
      }
    });

    // Destroy the instance on component unmount
    this.$on("hook:beforeDestroy", () => {
      gstc.destroy();
    });
  }
};
